import React, { useState } from "react";

import Button from "../../UI/Button/Button";
import "./CourseInput.css";

const CourseInput = (props) => {
  const [enteredValue, setEnteredValue] = useState("");
  const [isInputValid, setIsInputValid] = useState(true);

  const goalInputChangeHandler = (event) => {
    let goalInputValue = event.target.value;
    if (goalInputValue.length > 0) {
      setEnteredValue(goalInputValue);
      setIsInputValid(true);
    } else {
      setIsInputValid(false);
    }
  };

  const formSubmitHandler = (event) => {
    event.preventDefault();
    if (enteredValue.trim().length === 0) {
      setIsInputValid(false);
      return;
    }
    setEnteredValue("");
    props.onAddGoal(enteredValue);
  };

  return (
    <form onSubmit={formSubmitHandler}>
      <div className={`form-control ${isInputValid ? 'invalid' : ''}`}>
        <label style={{ color: !isInputValid ? 'red' : 'black' }}>
          Course Goal
        </label>
        <input
          style={{
            borderColor: !isInputValid ? 'red' : '#ccc',
            background: !isInputValid ? 'salmon' : 'transparent',
          }}
          type="text"
          value={enteredValue}
          onChange={goalInputChangeHandler}
        />
      </div>
      <Button type="submit">Add Goal</Button>
    </form>
  );
};

export default CourseInput;
